00049 static void region_grow( PImageDouble angles, PImageInt used, Point *reg, 
00050                          int start, int idx_buff, int *end, int label, 
00051                          int label_start, double prec, double *reg_angle )
00052 {
00053   double sumdx = 0.0, sumdy = 0.0;
00054   double ang;
00055   int xx, yy, i;
00056   int adr;
00057   /* check parameters */
00058   if( (angles == NULL) || (angles->data == NULL) )
00059     error("region_grow: invalid image 'angles'.");
00060   if( (used == NULL) || (used->data == NULL) )
00061     error("region_grow: invalid image 'used'.");
00062   if( reg == NULL ) error("region_grow: invalid input region.");
00063   /* if( start >= *end ) error("region_grow: invalid indexes.");*/
00064   if( prec < 0.0 ) error("region_grow: 'prec' must be positive.");
00065   
00066   /* Initialise *reg_angle with the value of the pixels 
00067      orientations in the buffer part of reg. */
00068  
00069   for( i=start; i<idx_buff; i++ )
00070     {
00071       xx = reg[i].x; yy = reg[i].y;
00072       adr = xx + yy*angles->xsize;
00073       sumdx += cos( angles->data[adr] );
00074       sumdy += sin( angles->data[adr] );
00075       used->data[adr] = label;
00076     }
00077   *reg_angle = atan2( sumdy, sumdx );
00078   /* try neighbors as new region points */
00079   for( i=start; i<*end; i++ )
00080     for( xx=reg[i].x-1; xx<=reg[i].x+1; xx++ )
00081       for( yy=reg[i].y-1; yy<=reg[i].y+1; yy++ )
00082         if( in_image( xx, yy, used->xsize, used->ysize ) )
00083           {
00084             adr = yy * angles->xsize + xx;
00085             if( (used->data[adr] != USED) && (used->data[adr] != label) && 
00086                 (used->data[adr] < label_start) )
00087               {
00088                 ang = angles->data[adr];
00089                 if( (ang != NOTDEF ) && (is_aligned( ang, *reg_angle, prec )) )
00090                   {
00091                     /* add point and mark as already visited in this call */
00092                     used->data[adr] = label;            
00093                     reg[*end].x = xx;
00094                     reg[*end].y = yy;
00095                     ++(*end);             
00096                     /* update region's angle */
00097                     sumdx += cos(ang);
00098                     sumdy += sin(ang);
00099                     *reg_angle = atan2( sumdy, sumdx );
00100                   }
00101          
00102               }
00103           }
00104 }
