00123 static double get_theta( PImageDouble gradmag, Point *reg, int start, int end, 
00124                          double cx, double cy, double reg_angle, double prec )
00125 {
00126   double lambda1, lambda2, tmp, theta, weight, sum;
00127   double Ixx = 0.0;
00128   double Iyy = 0.0;
00129   double Ixy = 0.0;
00130   int i;
00131   /* check parameters */
00132   if( reg == NULL ) error("get_theta: invalid input region.");
00133   if( (gradmag == NULL) || (gradmag->data == NULL) )
00134     error("get_theta: invalid image 'gradmag'.");
00135   if( start >= end ) error("get_theta: invalid indexes.");
00136   if( prec < 0.0 ) error("get_theta: 'prec' must be positive.");
00137 
00138   /*----------- theta ---------------------------------------------------*/
00139   /*
00140       Region inertia matrix A:
00141          Ixx Ixy
00142          Ixy Iyy
00143       where
00144         Ixx = \sum_i y_i^2
00145         Iyy = \sum_i x_i^2
00146         Ixy = -\sum_i x_i y_i
00147 
00148       lambda1 and lambda2 are the eigenvalues, with lambda1 >= lambda2.
00149       They are found by solving the characteristic polynomial
00150       det(\lambda I - A) = 0.
00151 
00152       To get the line segment direction we want to get the eigenvector of
00153       the smaller eigenvalue. We have to solve a,b in:
00154         a.Ixx + b.Ixy = a.lambda2
00155         a.Ixy + b.Iyy = b.lambda2
00156       We want the angle theta = atan(b/a). I can be computed with
00157       any of the two equations:
00158         theta = atan( (lambda2-Ixx) / Ixy )
00159       or
00160         theta = atan( Ixy / (lambda2-Iyy) )
00161 
00162       When |Ixx| > |Iyy| we use the first, otherwise the second
00163       (just to get better numeric precision).
00164    */
00165   sum = 0.0;
00166   for( i=start; i<end; i++ )
00167     {
00168       weight = gradmag->data[ reg[i].x + reg[i].y * gradmag->xsize ];
00169       Ixx += ( (double) reg[i].y - cy ) * ( (double) reg[i].y - cy ) * weight;
00170       Iyy += ( (double) reg[i].x - cx ) * ( (double) reg[i].x - cx ) * weight;
00171       Ixy -= ( (double) reg[i].x - cx ) * ( (double) reg[i].y - cy ) * weight;
00172       sum += weight;
00173     }
00174   if( sum <= 0.0 ) error("get_theta: weights sum less or equal to zero.");
00175   Ixx /= sum;
00176   Iyy /= sum;
00177   Ixy /= sum;
00178   lambda1 = ( Ixx + Iyy + sqrt( (Ixx-Iyy)*(Ixx-Iyy) + 4.0*Ixy*Ixy ) ) / 2.0;
00179   lambda2 = ( Ixx + Iyy - sqrt( (Ixx-Iyy)*(Ixx-Iyy) + 4.0*Ixy*Ixy ) ) / 2.0;
00180   if( fabs(lambda1) < fabs(lambda2) )
00181     {
00182       fprintf(stderr,"Ixx %g Iyy %g Ixy %g lamb1 %g lamb2 %g - lamb1 < lamb2\n",
00183               Ixx, Iyy, Ixy, lambda1, lambda2 );
00184       tmp = lambda1;
00185       lambda1 = lambda2;
00186       lambda2 = tmp;
00187     }
00188 
00189   if( fabs(Ixx) > fabs(Iyy) )
00190     theta = atan2( lambda2-Ixx, Ixy );
00191   else
00192     theta = atan2( Ixy, lambda2-Iyy );
00193 
00194   /* The previous procedure doesn't care about orientation,
00195      so it could be wrong by 180 degrees. Here is corrected if necessary. */
00196   if( angle_diff( theta-M_1_2_PI, reg_angle ) > prec ) theta += M_PI;
00197 
00198   /* If the estimated orientation is still very different wrt reg_angle
00199      (region is nearly square), return the orthogonal to original reg_angle. */
00200   if( angle_diff( theta-M_1_2_PI, reg_angle ) > prec ) 
00201     theta = reg_angle + M_1_2_PI;
00202 
00203   while( theta >  M_PI ) theta -= M_2__PI;
00204   while( theta < -M_PI ) theta += M_2__PI;
00205   return theta;
00206 }
