00214 void region2rect( PImageDouble gradmag, Point *reg, int start, int end,
00215                          double reg_angle, Rectangle *rec, double prec )
00216 {
00217   double cx, cy, dx, dy, l, w, theta;
00218   double weight,sum,l_min,l_max,w_min,w_max;
00219   int i, adr;
00220   /* Check parameters */
00221   if( (gradmag == NULL) || (gradmag->data == NULL) )
00222     error("region2rect: invalid image 'gradmag'.");
00223   if( reg == NULL ) error("region2rect: invalid input region.");
00224   if( start >= end ) error("region2rect: invalid indexes."); 
00225   if( rec == NULL ) error("region2rect: invalid 'rec'.");
00226 
00227   /* Center */
00228   cx = cy = sum = 0.0;
00229   for( i=start; i<end; i++ )
00230     {
00231       adr = reg[i].x + reg[i].y * gradmag->xsize;
00232       weight = gradmag->data[adr];
00233       cx += (double) reg[i].x * weight;
00234       cy += (double) reg[i].y * weight;
00235       sum += weight;
00236     }
00237 
00238   cx /= sum;
00239   cy /= sum;
00240 
00241   /* Orientation */  
00242   theta = get_theta( gradmag, reg, start, end, cx, cy, reg_angle, prec );
00243 
00244   /* Length and width */
00245   dx = cos(theta);
00246   dy = sin(theta);
00247   l_min = l_max = w_min = w_max = 0.0;
00248   for( i=start; i<end; i++ )
00249     {
00250       l =  ( (double) reg[i].x - cx) * dx + ( (double) reg[i].y - cy) * dy;
00251       w = -( (double) reg[i].x - cx) * dy + ( (double) reg[i].y - cy) * dx;
00252 
00253       if( l > l_max ) l_max = l;
00254       if( l < l_min ) l_min = l;
00255       if( w > w_max ) w_max = w;
00256       if( w < w_min ) w_min = w;
00257     }
00258 
00259   /* Store values */
00260   rec->x1 = cx + l_min * dx;
00261   rec->y1 = cy + l_min * dy;
00262   rec->x2 = cx + l_max * dx;
00263   rec->y2 = cy + l_max * dy;
00264   rec->width = w_max - w_min;
00265   rec->wmin = w_min;
00266   rec->wmax = w_max;
00267   rec->cx = cx;
00268   rec->cy = cy;
00269   rec->theta = theta;
00270   rec->dx = dx;
00271   rec->dy = dy;
00272   rec->prec = prec;
00273   rec->len = dist( rec->x1, rec->y1, rec->x2, rec->y2 );
00274   /* Correct if rectangle too thin */
00275   if( rec->width < 1.0 )
00276     { 
00277       rec->width = 1.0; rec->wmin = -0.5; rec->wmax = 0.5;
00278     }
00279 }
