00287 static int reduce_region_radius( PImageDouble gradmag, PImageDouble angles,
00288                                  PImageInt used, Point *reg, int start, 
00289                                  int *end, double reg_angle, Rectangle *rec, 
00290                                  int x, int y, double density_th, double prec )
00291 {
00292   double density, rad1, rad2, rad;
00293   int i;
00294   /* Check parameters */
00295   if( (gradmag == NULL) || (gradmag->data == NULL) )
00296     error("reduce_region_radius: invalid image 'gradmag'.");
00297   if( (angles == NULL) || (angles->data == NULL) )
00298     error("reduce_region_radius: invalid image 'angles'.");
00299   if( (used == NULL) || (used->data == NULL) )
00300     error("reduce_region_radius: invalid image 'used'.");
00301   if( reg == NULL ) error("reduce_region_radius: invalid input region.");
00302   if( start >= *end ) error("reduce_region_radius: invalid indexes."); 
00303   if( rec == NULL ) error("reduce_region_radius: invalid 'rec'.");
00304   if( prec < 0.0 ) error("reduce_region_radius: 'prec' must be positive.");
00305 
00306   /* Compute region points density */
00307   density = (double) (*end - start) /
00308                      (dist(rec->x1,rec->y1,rec->x2,rec->y2) * rec->width);
00309 
00310   if( density >= density_th ) return TRUE;
00311 
00312   /* Compute region radius */
00313   rad1 = dist( (double)x, (double)y, rec->x1, rec->y1 );
00314   rad2 = dist( (double)x, (double)y, rec->x2, rec->y2 );
00315   rad = rad1 > rad2 ? rad1 : rad2;
00316 
00317   while( density < density_th )
00318     {
00319       rad *= 0.75;
00320 
00321       /* Remove points from the region and update 'used' map */
00322       for( i=start; i<*end; i++ )
00323         if( dist( x, y, (double) reg[i].x, (double) reg[i].y ) > rad )
00324           {
00325             /* Point not kept, mark it as NOTUSED */
00326             used->data[ reg[i].x + reg[i].y * used->xsize ] = NOTUSED;
00327             /* Remove point from the region */
00328             reg[i].x = reg[*end-1].x; /* if i==*end-1 copy itself */
00329             reg[i].y = reg[*end-1].y;
00330             --(*end);
00331             --i; /* to avoid skipping one point */
00332           }
00333       /* Reject if the region is too small.
00334          2 is the minimal region size for 'region2rect' to work. */
00335 
00336       if( (*end - start) < 2 ) return FALSE;
00337 
00338       /* Re-compute rectangle */
00339       region2rect( gradmag, reg, start, *end, reg_angle, rec, prec );
00340 
00341       /* Re-compute region points density */
00342       density = (double) ( *end - start) /
00343                 ( dist( rec->x1, rec->y1, rec->x2, rec->y2 ) * rec->width );     
00344     }
00345   return TRUE;
00346 }
