00358 static int refine( PImageDouble gradmag, PImageDouble angles, PImageInt used, 
00359                    Point *reg, int start, int idx_buff, int *end, int *label,
00360                    int label_start, Rectangle *rec, double density_th, 
00361                    double prec )
00362 {
00363   double angle, ang_d, mean_angle, tau, density, ang_c, sum, s_sum, reg_angle;
00364   double gradmax;
00365   int i, n, xx, yy;
00366   int xc, yc;
00367   
00368   /* Check parameters */
00369   if( (gradmag == NULL) || (gradmag->data == NULL) ) 
00370     error("refine: invalid image 'gradmag'.");
00371   if( (angles == NULL) || (angles->data == NULL) ) 
00372     error("refine: invalid image 'angles'.");
00373   if( (used == NULL) || (used->data == NULL) ) 
00374     error("refine: invalid image 'used'.");
00375   if( reg == NULL ) error("refine: invalid input region.");
00376   if( start >= *end ) error("refine: invalid indexes."); 
00377   if( *label <= 1 ) error("refine: forbidden label value.");
00378   if( prec < 0.0 ) error("refine: 'prec' must be positive.");
00379   if( rec == NULL ) error("refine: invalid pointer 'rec'.");
00380  
00381   /* Compute region points density */
00382   density = (double) (*end - start) /
00383             ( dist(rec->x1,rec->y1,rec->x2,rec->y2) * rec->width );
00384 
00385   if( density >= density_th ) return TRUE;
00386   
00387   /*------ First try: reduce angle tolerance ------*/
00388 
00389   /* Compute the new mean angle and tolerance */
00390   gradmax = 0.0;
00391   xc = reg[start].x;
00392   yc = reg[start].y;
00393 
00394   for( i=start; i<idx_buff; i++ )
00395     {
00396       xx = reg[i].x; yy = reg[i].y;
00397       if( gradmag->data[yy*gradmag->xsize+xx] > gradmax )
00398         {
00399           xc = xx;
00400           yc = yy;
00401           gradmax = gradmag->data[yy*gradmag->xsize+xx];
00402         }
00403     }
00404   ang_c = angles->data[ xc + yc * angles->xsize ];
00405   sum = s_sum = 0.0;
00406   n = 0;
00407   for( i=start; i<*end; i++ )
00408     {
00409       used->data[ reg[i].x + reg[i].y * used->xsize ] = NOTUSED;
00410       if( dist( (double)xc,(double)yc,(double)reg[i].x,(double)reg[i].y ) 
00411           < rec->width )
00412         {
00413           angle = angles->data[ reg[i].x + reg[i].y * angles->xsize ];
00414           ang_d = angle_diff_signed(angle,ang_c);
00415           sum += ang_d;
00416           s_sum += ang_d *ang_d;
00417           ++n;
00418         }
00419     }
00420   mean_angle = sum / (double) n;
00421   /* tau = 2 * standard deviation */
00422   tau = max(2.0 * sqrt( (s_sum - 2.0 * mean_angle * sum) / (double) n
00423                          + mean_angle * mean_angle ),0.2); 
00424   tau = min(tau,prec);
00425   /*tau = 2.0 * sqrt( (s_sum - 2.0 * mean_angle * sum) / (double) n
00426                       + mean_angle*mean_angle );*/ /* 2 * standard deviation */
00427 
00428  
00429   /* Find a new region from the same starting point and new angle tolerance */
00430   *end = idx_buff;
00431   (*label)++;
00432   region_grow( angles, used, reg, start, idx_buff, end, *label, label_start, 
00433                tau, &reg_angle );
00434 
00435   /* If the region is too small, reject */
00436   if( *end - start <= 2 ) return FALSE;
00437 
00438   /* Re-compute rectangle */
00439   region2rect( gradmag, reg, start, *end, reg_angle, rec, prec );
00440 
00441   /* Re-compute region points density */
00442   density = (double) (*end - start) /
00443                      ( dist(rec->x1,rec->y1,rec->x2,rec->y2) * rec->width);
00444 
00445   /*------ Second try: reduce region radius ------*/
00446   if( density < density_th )
00447     return reduce_region_radius( gradmag, angles, used, reg, start, end, 
00448                                  reg_angle, rec, xc, yc, density_th, prec );
00449   return TRUE;
00450 }
