00463 static void px_seed( PImageDouble angles, PImageInt used, Rectangle *rec,  
00464                      Point *reg, int start, int *idx_buff, int end,
00465                      double *pext, double prec, int label, int label_start )
00466 {
00467   int xx, yy, i;
00468   int adr;
00469   int idx_buff0 = *idx_buff; 
00470   double width;
00471   double l1, l2, l;
00472   
00473   /* Check parameters */
00474   if( (angles == NULL) || (angles->data == NULL) )
00475     error("px_seed: invalid input 'angles' image.");
00476   if( (used == NULL) || (used->data == NULL) )
00477     error("px_seed: invalid input 'used' image.");
00478   if( rec == NULL ) error("px_seed: invalid input rectangle.");
00479   if( reg == NULL ) error("px_seed: invalid input region.");
00480   if( end <= start ) error("px_seed: invalid region size.");
00481 
00482   width = rec->width/2.0;
00483 
00484   /* Distance between rectangle centre and extreme point; a new point can be 
00485      a seed only if it is at a distance farther than this */ 
00486   l1 = fabs( (pext[0] - rec->cx) * rec->dx + (pext[1] - rec->cy) * rec->dy);
00487 
00488   /* Scan previous region to find valid points that are close to the end of 
00489      the rectangle; the new seed pixels will be among the neighbours of these 
00490      points. */
00491   
00492   for( i=start; i<end; i++ )
00493     {
00494       l = fabs( ( (double)reg[i].x - pext[0] ) * rec->dx + 
00495                 ( (double)reg[i].y - pext[1] ) * rec->dy);
00496 
00497       if( l<width )
00498         for( xx=reg[i].x-1; xx<=reg[i].x+1; xx++ )
00499           for( yy=reg[i].y-1; yy<=reg[i].y+1; yy++ )
00500             {
00501               /* If point in image */
00502               if( in_image( xx, yy, used->xsize, used->ysize ) )
00503                 {                   
00504                   adr = yy * used->xsize + xx;                  
00505                   /* If point not already used or visited or notdef */
00506                   if( (used->data[adr] != USED) && (used->data[adr] != label ) &&
00507                       (angles->data[adr] != NOTDEF) && used->data[adr] < label_start )
00508                     {
00509                       l2 = fabs( (xx - rec->cx) * rec->dx + 
00510                                  (yy - rec->cy) * rec->dy ); 
00511                       /* If point's orientation is similar and the point is at
00512                          an acceptable distance  */
00513                       if( (angle_diff( angles->data[adr], 
00514                            rec->theta-M_1_2_PI ) < 3 * prec ) && (l2 >= l1-1) )
00515                         {
00516                           reg[ *idx_buff ].x = xx;
00517                           reg[ *idx_buff ].y = yy;
00518                           ++(*idx_buff);
00519                           used->data[adr] = label;
00520                         }
00521                     }
00522                 }
00523             }
00524     }  
00525 
00526   /* If none of the visited pixels satisfy the conditions, take as seed 
00527      the end of the rectangle, if it is inside the image */
00528   if( idx_buff0 == *idx_buff )
00529     {
00530       xx = floor(pext[0]); yy = floor(pext[1]);
00531       if( in_image( xx, yy, used->xsize, used->ysize ) )
00532         {
00533           reg[ *idx_buff ].x = xx; 
00534           reg[ *idx_buff ].y = yy;
00535           ++(*idx_buff);
00536         }
00537     } 
00538 }
