00541 /*----------------------------------------------------------------------------*/
00547 static void subcurve( PImageDouble angles, PImageDouble gradmag, PImageInt used, 
00548                       PolyRect *poly, Point *reg, int start, int idx_buff,
00549                       int *end, double *pext, double *spir, double density_th,
00550                       double prec,int *sgn, int *label, int label_start, int w )
00551 {
00552   int convex = 1;
00553   double reg_angle, difang, ang_prev;
00554   double lenrec, lenth;
00555   int buf_tmp = 0;
00556   Rectangle rec;
00557   
00558   /* Check parameters */
00559   if( (angles == NULL) || (angles->data == NULL) )
00560     error("subcurve: invalid input 'angles' image.");
00561   if( (gradmag == NULL) || (gradmag->data == NULL) )
00562     error("subcurve: invalid input 'gradmag' image.");
00563   if( (used == NULL) || (used->data == NULL) )
00564     error("subcurve: invalid input 'used' image.");
00565   if( reg == NULL ) error("subcurve: invalid input region.");
00566   if( *end <= start ) error("subcurve: invalid region size.");
00567   if( *label <= 1 ) error("subcurve: forbidden label value.");
00568 
00569 
00570   /* Get length and angle of initial rectangle (stored in polygon on first 
00571      position) */
00572   lenth = poly->rectlist[0].len;
00573   ang_prev = poly->rectlist[0].theta;
00574  
00575   /* Add new rectangles while the conditions of smoothness and convexity 
00576      are satisfied */
00577   while( convex )
00578     {
00579       (*label)++;
00580       start = *end;
00581       *end = idx_buff;
00582       buf_tmp = *end - start;
00583       /* Start region grow at the end of the previous rectangle */
00584       region_grow( angles, used, reg, start, idx_buff, end, *label, label_start,
00585                    prec, &reg_angle );       
00586       if( *end - start > buf_tmp )
00587         {
00588           /* Estimate and refine rectangle */
00589           region2rect( gradmag, reg, start, *end, reg_angle, &rec, prec );
00590           (*label)++;
00591           if( !refine( gradmag, angles, used, reg, start, idx_buff, end,
00592                        label, label_start, &rec, density_th, prec) ) 
00593             {
00594               /* If operation not successful, clean last visited points and 
00595                  return */
00596               mark_img_pts( used, reg, start, *end, NOTUSED );
00597               *end = start;
00598               break;
00599             }
00600           if( *end - start <= buf_tmp ) 
00601             {
00602               *end = start;
00603               break;
00604             }
00605 
00606           /* Convexity rule imposes that consecutive rectangles turn in the same
00607              direction, hence must have the same sign for angle difference 
00608              between consecutive rectangles' orientation. If set, verify sign
00609              before adding a new rectangle. If not set (at first call), 
00610              then set sign. */
00611           if( *sgn == 0 ) 
00612             *sgn = sign( angle_diff_signed( rec.theta, ang_prev ) ); 
00613 
00614           /* Get angle difference between current rectangle and previous 
00615              rectangle */  
00616           difang = angle_diff_signed( rec.theta, ang_prev );
00617 
00618           /* If consecutive rectangles don't have comparable lengths  
00619              (condition for smoothness): clean and return; 
00620              Special case: last rectangle that joins the ends of a full 
00621              chain can be shorter than previous.  */
00622           lenrec = rec.len;
00623           if( lenrec > lenth*3.0 || 
00624               (lenrec < lenth/3.0 && (*spir + fabs(difang)) <= 0.9*M_2__PI) ) 
00625             {
00626               mark_img_pts( used, reg, start, *end, NOTUSED );
00627               *end = start;
00628               break;
00629             }
00630           lenth = lenrec;
00631 
00632 
00633           /* Convexity & non-spiral check */
00634           if( (sign(difang) == *sgn) && (fabs(difang) < (M_PI/3.0) ) &&
00635               ( (*spir = *spir + fabs(difang)) <= M_2__PI) )
00636             { 
00637               /* If all conditions are met, find next seed point, 
00638                  and add rectangle to polygon */
00639               (*label)++;
00640               idx_buff = *end;
00641               if( w==0 ) {pext[0] = rec.x1; pext[1] = rec.y1;}
00642               else {pext[0] = rec.x2; pext[1] = rec.y2;}
00643               px_seed( angles, used, &rec, reg, start, &idx_buff, *end,
00644                        pext, prec, *label, label_start );
00645 
00646               /* Add rectangle to polygon */
00647               poly->dim++;
00648               add_rect_to_polyrect( poly, &rec );
00649               ang_prev = rec.theta; 
00650             }      
00651           else /* Not convex */
00652             {
00653               convex = 0;
00654               /* Subtract the contribution of the last rectangle and clean */
00655               mark_img_pts( used, reg, start, *end, NOTUSED );
00656               *end = start;
00657             }
00658         } /* !if( *end - start > 1 ) */
00659       else 
00660         { 
00661           convex = 0;
00662           *end = start;
00663         }     
00664     }  /* !while(convex) */
00665 } 
00666 
00667 
00668 /*----------------------------------------------------------------------------*/
00672 int curve_grow( PImageDouble gradmag, PImageDouble angles, 
00673                        PImageInt used, Point *reg, int *reg_size, 
00674                        double density_th, double prec, PolyRect *poly, 
00675                        int *label, double *pext1, double *pext2, double *spir ) 
00676 {
00677   int sgn = 0;
00678   Rectangle rec;
00679   int size_first_reg = 0;
00680   int label_start;
00681   int start, idx_buff;
00682   double reg_angle;
00683   
00684   /* Check parameters */
00685   if( (gradmag == NULL) || (gradmag->data == NULL) )
00686     error("curve_grow: invalid input 'gradmag' image.");
00687   if( (angles == NULL) || (angles->data == NULL) )
00688     error("curve_grow: invalid input 'angles' image.");
00689   if( (used == NULL) || (used->data == NULL) )
00690     error("curve_grow: invalid input 'used' image.");
00691   if( reg == NULL ) error("curve_grow: invalid input region.");
00692   if( *reg_size <=0 ) error("curve_grow: list must contain at least 1 point.");
00693   if( poly == NULL ) error("curve_grow: poly must be non null.");
00694   if( *label <= 1 ) error("curve_grow: forbidden label value.");
00695   
00696   /* Init indexes */
00697   start = 0;
00698   idx_buff = *reg_size;
00699   label_start = (*label);
00700   /* Perform initial region growing, to estimate first rectangle */
00701   region_grow( angles, used, reg, start, idx_buff, reg_size, *label, label_start,
00702                prec, &reg_angle ); 
00703 
00704   if( *reg_size <= 2 ) return 0;
00705   
00706   /* Estimate initial rectangle */
00707   region2rect( gradmag, reg, start, *reg_size, reg_angle, &rec, prec );
00708   
00709   /* Refine rectangle if density of aligned points is less than density 
00710      threshold */
00711   if( !refine( gradmag, angles, used, reg, start, idx_buff, reg_size, label, 
00712                label_start, &rec, density_th, prec ) )
00713     {
00714       mark_img_pts( used, reg, start, *reg_size, NOTUSED );
00715       return 0;
00716     }
00717   if( rec.len <= 2 ) return 0;
00718 
00719   /* Save size of first region; useful to compute second seed, after scanning
00720      first direction */
00721   size_first_reg = *reg_size;
00722 
00723   /* Add rectangle to poly */
00724   poly->dim++; 
00725   add_rect_to_polyrect( poly, &rec );
00726 
00727   /* Compute first seed with the first end of the initial rectangle */
00728   pext1[0] = rec.x2; 
00729   pext1[1] = rec.y2;
00730   (*label)++;
00731   idx_buff = *reg_size;
00732   px_seed( angles, used, &rec, reg, start, &idx_buff, *reg_size, pext1, prec,
00733            *label, label_start ); 
00734 
00735   /* Lauch scan in first direction */ 
00736   subcurve( angles, gradmag, used, poly, reg, start, idx_buff, reg_size, 
00737             pext1, spir, density_th, prec, &sgn, label, label_start, 1 ); 
00738   
00739   /* Scan the other direction only if a complete tour was not scanned; this
00740      condition is to avoid spiral curves */
00741   pext2[0] = rec.x1; 
00742   pext2[1] = rec.y1; 
00743   if( *spir < M_2__PI )
00744     {
00745       /* Compute second seed with the second end of the initial rectangle */  
00746  
00747       idx_buff = *reg_size;
00748       (*label)++;
00749       px_seed( angles, used, &rec, reg, 0, &idx_buff, size_first_reg, 
00750                pext2, prec, *label, label_start );
00751       /* When scanning the other direction, the sign must be inverted */ 
00752       sgn = -sgn;
00753       /* Lauch scan in second direction */ 
00754       subcurve( angles, gradmag, used, poly, reg, start, idx_buff, reg_size, 
00755                 pext2, spir, density_th, prec, &sgn, label, label_start, 0 );
00756     }    
00757   return 1;
00758 }
