00051 #define LOG10_1_2_M_2__PI  0.39908993418
00052 #define LOG10_EXP1         0.43429448190
00053 static double NFAc( int n, double k, double logNT )
00054 {
00055   /* check parameters */
00056   if( n < 0) error("NFAc: 'n' must be strictly positive.");
00057   if( n==0 ) return -logNT;
00058   double logNFAC = logNT;
00059   
00060   k = max(k,0.01);
00061   logNFAC = logNT + n * log10(k) - LOG10_1_2_M_2__PI - 
00062             (n+0.5) * log10(n) + n * LOG10_EXP1 ;
00063 
00064   return -logNFAC;
00065 }
