00078 static void rect_count_error( PImageDouble angles, PImageInt used, 
00079                               Rectangle *rec, int *pts, double *err, 
00080                               Point *buff, int *size_buff )
00081 {
00082   RectIter *i;
00083   int adr;
00084   double theta;
00085  
00086   /* check parameters */
00087   if( (angles == NULL) || (angles->data == NULL) )
00088     error("rect_count_error: invalid input 'angles' image.");
00089   if( (used == NULL) || (used->data == NULL) )
00090     error("rect_count_error: invalid input 'used' image.");
00091   if( rec == NULL ) error("rect_count_error: invalid input rectangle.");
00092   if( buff == NULL ) error("rect_count_error: invalid buffer.");
00093   if( size_buff == NULL ) 
00094     error("rect_count_error: size_buff must be non null.");
00095 
00096   theta = rec->theta - M_1_2_PI;
00097   *pts = 0; *err = 0.0;
00098 
00099   /* iterate through rectangle and accumulate per-pixel normalised error */
00100   for( i=ini_RectIter(rec); !end_RectIter(i); inc_RectIter(i) )
00101     if( in_image( i->x, i->y, used->xsize, used->ysize ) ) 
00102       {
00103         ++(*pts);
00104         adr = (i->y) * used->xsize + (i->x);
00105         if( (used->data[adr] == USED) || (angles->data[adr] == NOTDEF) )
00106           (*err) += 1.0;
00107         else
00108           (*err) += norm_angle_diff( angles->data[adr], theta );
00109         /* store points in buffer */
00110         buff[*size_buff].x = i->x;
00111         buff[*size_buff].y = i->y;
00112         ++(*size_buff);
00113       }
00114   free_RectIter(i);
00115 }
