00122 static void get_seg( PImageDouble angles, PImageDouble gradmag, 
00123                      PImageInt used, PolyRect *poly, Point *reg, 
00124                      int reg_size, double prec )
00125 {
00126   double sumdx = 0.0, sumdy = 0.0;
00127   double reg_angle;
00128   int i;
00129   int adr;
00130   Rectangle rec;
00131   /* check parameters */
00132   if( (angles == NULL) || (angles->data == NULL) )
00133     error("get_seg: invalid input 'angles' image.");
00134   if( (gradmag == NULL) || (gradmag->data == NULL) )
00135     error("get_seg: invalid input 'angles' image.");
00136   if( (used == NULL) || (used->data == NULL) )
00137     error("get_seg: invalid input 'used' image.");
00138   if( poly == NULL ) error("get_seg: invalid input polygon.");
00139   if( reg == NULL ) error("get_seg: invalid input region.");
00140   if( reg_size <= 0 ) error("get_seg: 'reg_size' must be strictly positive.");
00141   
00142   /* compute the mean angle of the whole region */
00143   for( i=0; i<reg_size; i++ )
00144     {
00145       adr = reg[i].x + reg[i].y * angles->xsize;
00146       sumdx += cos( angles->data[adr] );
00147       sumdy += sin( angles->data[adr] );
00148     }
00149   reg_angle = atan2( sumdy, sumdx );
00150 
00151   /* estimate rectangle */
00152   region2rect( gradmag, reg, 0, reg_size, reg_angle, &rec, prec ); 
00153   
00154   /* add rectangle to polygon */
00155   poly->dim++;
00156   add_rect_to_polyrect( poly, &rec );  
00157 }
