00170 static void poly_count_error( PImageDouble angles, PImageInt used, 
00171                               PolyRect *poly, double polywmin, double polywmax,
00172                               int *pts, double *err, Point *buff, 
00173                               int *size_buff ) 
00174 {
00175   int pts_rect;
00176   double err_rect;
00177   int i;
00178   /* check parameters */
00179   if( (angles == NULL) || (angles->data == NULL) )
00180     error("poly_count_error: invalid input 'angles' image.");
00181   if( (used == NULL) || (used->data == NULL) )
00182     error("poly_count_error: invalid input 'used' image.");
00183   if( poly == NULL ) error("poly_count_error: invalid input polygon.");
00184   if( buff == NULL ) error("poly_count_error: invalid buffer.");
00185   if( size_buff == NULL ) 
00186     error("poly_count_error: size_buff must be non null.");
00187 
00188   *pts = 0; *err = 0.0;
00189   *size_buff = 0;
00190 
00191   /* Scan every rectangle in the polygon and count the additive error. 
00192      We must consider the same widths wmin and wmax for all rectangles. */
00193   for( i=0; i<poly->dim; i++)
00194     { 
00195       poly->rectlist[i].wmin = polywmin;
00196       poly->rectlist[i].wmax = polywmax;
00197       rect_count_error( angles, used, &(poly->rectlist[i]), &pts_rect, 
00198                         &err_rect, buff, size_buff );
00199       (*pts) += pts_rect;
00200       (*err) += err_rect;
00201     }  
00202 }
