00208 static double poly_nfa( PImageDouble angles, PImageInt used, PolyRect *poly, 
00209                         double polywmin, double polywmax, Point *buff, 
00210                         int *size_buff, double logNT_poly )
00211 {
00212   int pts;
00213   double err;
00214   
00215   /* check parameters */
00216   if( (angles == NULL) || (angles->data == NULL) )
00217     error("poly_nfa: invalid input 'angles' image.");
00218   if( (used == NULL) || (used->data == NULL) )
00219     error("poly_nfa: invalid input 'used' image.");
00220   if( poly == NULL ) error("poly_nfa: invalid input polygon.");
00221   if( buff == NULL ) error("poly_nfa: invalid buffer.");
00222   if( size_buff == NULL ) 
00223     error("poly_nfa: size_buff must be non null.");
00224 
00225   /* count additive error */
00226   poly_count_error( angles, used, poly, polywmin, polywmax, &pts, &err, buff, 
00227                     size_buff );
00228   /* return computed NFA */
00229   return NFAc( pts, err, logNT_poly );
00230 }
