00233 /*----------------------------------------------------------------------------*/
00236 static double poly_improve( PImageDouble angles, PImageInt used, PolyRect *poly,
00237                             Point **local_buff, int *size_local_buff, 
00238                             Point **tmp_buff, double logNT_poly )
00239 {
00240   double delta = 0.5;
00241   double log_nfa,log_nfa_new;
00242 
00243   int size_tmp_buff;
00244   double wmin,wmax;
00245   
00246   /* check parameters */
00247   if( (angles == NULL) || (angles->data == NULL) )
00248     error("poly_improve: invalid input 'angles' image.");
00249   if( (used == NULL) || (used->data == NULL) )
00250     error("poly_improve: invalid input 'used' image.");
00251   if( poly == NULL ) error("poly_improve: invalid input polygon.");
00252   if( *local_buff == NULL ) error("poly_improve: invalid local buffer.");
00253   if( size_local_buff == NULL ) 
00254     error("poly_improve: 'size_local_buff' must be non null.");
00255   if( *tmp_buff == NULL ) error("poly_improve: invalid tmp buffer.");
00256  
00257   *size_local_buff = 0;
00258   wmax = poly->wmax;
00259   wmin = poly->wmin;
00260   /* check if polygon valid: wmin and wmax must have opposite sign */
00261   if ( wmin*wmax >0 ) return -logNT_poly;
00262 
00263   log_nfa = poly_nfa( angles, used, poly, wmin, wmax, *local_buff, 
00264                       size_local_buff, logNT_poly );
00265      
00266   /* try to improve the polygon, by reducing width; first reduce wmax */ 
00267   /*for( n=0; n<10; n++ )
00268     {*/      
00269       while( wmax - delta > 0.0)
00270         {
00271           wmax -= delta;
00272           size_tmp_buff = 0;
00273   
00274           log_nfa_new = poly_nfa( angles, used, poly, wmin, wmax, *tmp_buff, 
00275                                   &size_tmp_buff, logNT_poly );
00276           if( log_nfa_new > log_nfa )
00277             {
00278               log_nfa = log_nfa_new;
00279               /* switch addresses of the pointers to the local and tmp buffer */
00280               /*swap_ptr_pts( local_buff, tmp_buff );
00281               (*size_local_buff) = size_tmp_buff;*/
00282               poly->wmax -= delta;
00283             }  
00284         }
00285     /* } */
00286 
00287   /* now try to reduce dmin */
00288   /* for( n=0; n<10; n++ )
00289     { */      
00290       while( wmin + delta < 0.0)
00291         {
00292           wmin += delta;
00293           size_tmp_buff = 0;
00294         
00295           log_nfa_new = poly_nfa( angles, used, poly, wmin, poly->wmax, 
00296                                   *tmp_buff, &size_tmp_buff, logNT_poly );
00297           if( log_nfa_new > log_nfa )
00298             {
00299               log_nfa = log_nfa_new;
00300               /* switch addresses of the pointers to the local and tmp buffer */
00301               /* swap_ptr_pts( local_buff, tmp_buff );
00302               (*size_local_buff) = size_tmp_buff; */
00303               poly->wmin += delta;
00304             }    
00305         }
00306     /* } */
00307   return log_nfa;
00308 }
