00321 static void circ_count_error( PImageDouble angles, PImageInt used, Ring *cring,
00322                               int x, int y, int grad_dir, Point *buff, 
00323                               int *size_buff, int label, int *pts, double *err )
00324 {
00325   int xx, yy, i, adr;
00326   double theta;
00327  
00328   /* check parameters */
00329   if( (angles == NULL) || (angles->data == NULL) )
00330     error("circ_count_error: invalid input 'angles' image.");
00331   if( (used == NULL) || (used->data == NULL) )
00332     error("circ_count_error: invalid input 'used' image.");
00333   if( cring == NULL ) error("circ_count_error: invalid input ring.");
00334   if( buff == NULL ) error("circ_count_error: invalid local buffer.");
00335   if( size_buff == NULL ) 
00336     error("circ_count_error: 'size_local_buff' must be non null.");
00337   if( label <= 1 ) error("circ_count_error: forbidden label value.");
00338   
00339   *pts = 0; *err = 0.0;
00340   
00341   /* To compute the error, visit each pixel in the ring in a region growing 
00342      manner */
00343 
00344   /* First make sure the seed point (x,y) is inside the ring; if not, visit 
00345      neighbours in a 3x3 neighbourhood to find a point inside the ring */
00346   *size_buff = 0;
00347   if( is_in_circ_ring( cring, x, y ) )
00348     {
00349       buff[0].x = x;
00350       buff[0].y = y;
00351       used->data[ buff[0].y * used->xsize + buff[0].x ] = label;
00352       *size_buff = 1;
00353     }
00354   else
00355     {
00356       for( xx=x-2; xx<=x+2; xx++ )
00357         {
00358           for( yy=y-2; yy<=y+2; yy++ )
00359             if( in_image( xx, yy, used->xsize, used->ysize ) && is_in_circ_ring( cring, xx, yy ) )
00360               {
00361                 buff[0].x = xx;
00362                 buff[0].y = yy;
00363                 used->data[ buff[0].y * used->xsize + buff[0].x ] = label;
00364                 *size_buff = 1; 
00365                 break;
00366               }
00367           if( *size_buff == 1 ) break;
00368         } 
00369     }
00370   if( *size_buff == 0 ) return; 
00371 
00372   for( i=0; i < *size_buff; i++ )
00373     for( xx=buff[i].x-1; xx<=buff[i].x+1; xx++ )
00374       for( yy=buff[i].y-1; yy<=buff[i].y+1; yy++ )
00375         if( in_image( xx, yy, used->xsize, used->ysize ) )
00376           {
00377             adr = xx + yy * used->xsize;
00378             /* check if the point has already been used or visited */
00379             if( (used->data[adr] != USED) && (used->data[adr] != label) ) 
00380               {
00381                 /* check if point inside the circle ring */
00382                 if( is_in_circ_ring( cring, xx, yy ) )
00383                   {
00384                     /* check if gradient converges or diverges when computing 
00385                        the ideal angle of a point on the circle */
00386                     theta = atan2( (double)yy - cring->cy, 
00387                                    (double)xx - cring->cx );
00388                     if( grad_dir == 0 )
00389                       {
00390                 if( theta > 0 ) theta = -(M_PI-theta);
00391                 else theta = M_PI + theta;          
00392                   }
00393                     (*err) += norm_angle_diff( angles->data[adr], theta );
00394                     used->data[adr] = label;
00395                     buff[*size_buff].x = xx;
00396                     buff[*size_buff].y = yy;
00397                     ++(*size_buff); 
00398                   }
00399               }
00400           } 
00401   *pts = *size_buff;         
00402 }
