00408 static double circ_ring_nfa( PImageDouble angles, PImageInt used, Ring *cring,
00409                              int x, int y, int grad_dir, Point *buff, 
00410                              int *size_buff, int label, double logNT_ell )
00411 {
00412   int pts;
00413   double err;
00414  
00415   /* check parameters */
00416   if( (angles == NULL) || (angles->data == NULL) )
00417     error("circ_ring_nfa: invalid input 'angles' image.");
00418   if( (used == NULL) || (used->data == NULL) )
00419     error("circ_ring_nfa: invalid input 'used' image.");
00420   if( cring == NULL ) error("circ_ring_nfa: invalid input ring.");
00421   if( buff == NULL ) error("circ_ring_nfa: invalid local buffer.");
00422   if( size_buff == NULL ) 
00423     error("circ_ring_nfa: 'size_local_buff' must be non null.");
00424   if( label <= 1 ) error("circ_ring_nfa: forbidden label value.");
00425 
00426   /* count additive error */
00427   circ_count_error( angles, used, cring, x, y, grad_dir, buff, size_buff, 
00428                     label, &pts, &err );
00429 
00430   /* return computed NFA */
00431   return NFAc( pts, err, logNT_ell );
00432 }
