00529 static void ell_count_error( PImageDouble angles, PImageInt used, Ring *ering,
00530                              double *foci, int x, int y, int grad_dir, 
00531                              Point *buff, int *size_buff, int label, int *pts,
00532                              double *err )
00533 {
00534   int xx, yy, i, adr;
00535   double theta;
00536   
00537   /* check parameters */
00538   if( (angles == NULL) || (angles->data == NULL) )
00539     error("ell_count_error: invalid input 'angles' image.");
00540   if( (used == NULL) || (used->data == NULL) )
00541     error("ell_count_error: invalid input 'used' image.");
00542   if( ering == NULL ) error("ell_count_error: invalid input ring.");
00543   if( foci == NULL ) error("ell_count_error: invalid input foci.");
00544   if( buff == NULL ) error("ell_count_error: invalid local buffer.");
00545   if( size_buff == NULL ) 
00546     error("ell_count_error: 'size_local_buff' must be non null.");
00547   if( label <= 1 ) error("ell_count_error: forbidden label value.");
00548   
00549   *pts = 0; *err = 0.0;
00550 
00551   /* To compute the error, visit each pixel in the ring in a region growing 
00552      manner */
00553   /* First make sure the seed point (x,y) is inside the ring; if not, visit 
00554      neighbours in a 3x3 neighbourhood to find a point inside the ring */
00555   *size_buff = 0;
00556   if( is_in_ell_ring( ering, x, y ) )
00557     {
00558       buff[0].x = x;
00559       buff[0].y = y;
00560       used->data[ buff[0].y * used->xsize + buff[0].x ] = label;
00561       *size_buff = 1;
00562     }
00563   else
00564     {
00565       for( xx=x-2; xx<=x+2; xx++ )
00566         {
00567           for( yy=y-2; yy<=y+2; yy++ )
00568             if( in_image( xx, yy, used->xsize, used->ysize ) && is_in_ell_ring( ering, xx, yy ) )
00569               {
00570                 buff[0].x = xx;
00571                 buff[0].y = yy;
00572                 used->data[ buff[0].y * used->xsize + buff[0].x ] = label;
00573                 *size_buff = 1; 
00574                 break;
00575               }
00576           if( *size_buff == 1 ) break;
00577         } 
00578     }
00579   if( *size_buff == 0 ) return;  
00580 
00581   for( i=0; i < *size_buff; i++ )
00582     for( xx=buff[i].x-1; xx<=buff[i].x+1; xx++ )
00583       for( yy=buff[i].y-1; yy<=buff[i].y+1; yy++ )
00584         if( in_image( xx, yy, used->xsize, used->ysize ) )
00585           {
00586             adr = xx + yy * used->xsize;
00587             /* check if the point has already been used or visited */
00588             if( (used->data[adr] != USED) && (used->data[adr] != label) ) 
00589               {
00590                 /* check if point inside the ellipse ring */                
00591                 if( is_in_ell_ring( ering, xx, yy ) )
00592                   {
00593                     /* get angle normal to the ellipse */
00594                     theta = ellipse_normal_angle( (double)xx, (double)yy, foci);
00595                     /* check if gradient converges or diverges when computing 
00596                        the ideal angle of a point on the circle */
00597                     if( grad_dir == 0 )
00598                       {
00599                 if( theta > 0 ) theta = - ( M_PI - theta );
00600                 else theta = M_PI + theta;          
00601                   }
00602                     (*err) += norm_angle_diff( angles->data[adr], theta );
00603                     used->data[adr] = label;
00604                     buff[*size_buff].x = xx;
00605                     buff[*size_buff].y = yy;
00606                     ++(*size_buff); 
00607                   }
00608               }
00609           } 
00610   *pts = *size_buff;     
00611 }
00612 
