00614 /*----------------------------------------------------------------------------*/
00617 static double ell_ring_nfa( PImageDouble angles, PImageInt used, Ring *ering,
00618                             double *foci, int x, int y, int grad_dir, 
00619                             Point *buff, int *size_buff, int label, 
00620                             double logNT_ell )
00621 {
00622   int pts;
00623   double err;
00624   
00625   /* check parameters */
00626   if( (angles == NULL) || (angles->data == NULL) )
00627     error("ell_ring_nfa: invalid input 'angles' image.");
00628   if( (used == NULL) || (used->data == NULL) )
00629     error("ell_ring_nfa: invalid input 'used' image.");
00630   if( ering == NULL ) error("ell_ring_nfa: invalid input ring.");
00631   if( foci == NULL ) error("ell_ring_nfa: invalid input foci.");
00632   if( buff == NULL ) error("ell_ring_nfa: invalid local buffer.");
00633   if( size_buff == NULL ) 
00634     error("ell_ring_nfa: 'size_local_buff' must be non null.");
00635   if( label <= 1 ) error("ell_ring_nfa: forbidden label value.");
00636 
00637   /* count additive error */
00638   ell_count_error( angles, used, ering, foci, x, y, grad_dir, buff, size_buff,
00639                    label, &pts, &err );
00640 
00641   /* return computed NFA */
00642   return NFAc( pts, err, logNT_ell );
00643 }
