00646 /*----------------------------------------------------------------------------*/
00649 static double ell_ring_improve( PImageDouble angles, PImageInt used, 
00650                                 Ring *ering, double *foci, int x, int y, 
00651                                 int grad_dir, Point **local_buff, 
00652                                 int *size_local_buff, Point **tmp_buff, 
00653                                 int *label, double logNT_ell )
00654 { 
00655   double log_nfa, log_nfa_new;
00656   double delta = 0.5;
00657   int size_tmp_buff;
00658 
00659   /* check parameters */
00660   if( (angles == NULL) || (angles->data == NULL) )
00661     error("ell_ring_improve: invalid input 'angles' image.");
00662   if( (used == NULL) || (used->data == NULL) )
00663     error("ell_ring_improve: invalid input 'used' image.");
00664   if( ering == NULL ) error("ell_ring_improve: invalid input ring.");
00665   if( foci == NULL ) error("ell_ring_improve: invalid input foci.");
00666   if( local_buff == NULL ) error("ell_ring_improve: invalid local buffer.");
00667   if( tmp_buff == NULL ) error("ell_ring_improve: invalid tmp buffer.");
00668   if( size_local_buff == NULL ) 
00669     error("ell_ring_improve: 'size_local_buff' must be non null.");
00670   if( *label <= 1 ) error("ell_ring_improve: forbidden label value.");
00671 
00672   if ( ! check_ell_ring(ering) ) return -logNT_ell;
00673   (*label)++;
00674   *size_local_buff = 0;
00675   log_nfa = ell_ring_nfa( angles, used, ering, foci, x, y, grad_dir, 
00676                           *local_buff, size_local_buff, *label, logNT_ell );
00677      
00678   /* try to improve the ring, by reducing width; first reduce wmax */
00679   Ring r; 
00680   copy_ring( ering, &r );
00681   /*for( n=0; n<10; n++ )
00682     { */        
00683       while( r.wmax-delta > 0.0) 
00684         {
00685           r.wmax -= delta;
00686           size_tmp_buff = 0;
00687           (*label)++;
00688         
00689           log_nfa_new = ell_ring_nfa( angles, used, &r, foci, x, y, grad_dir,
00690                                       *tmp_buff, &size_tmp_buff, *label,
00691                                       logNT_ell );
00692           if( log_nfa_new > log_nfa )
00693             {
00694               ering->wmax -= delta;
00695               log_nfa = log_nfa_new;
00696               /* switch addresses of the pointers to the local and tmp buffer */
00697               /*swap_ptr_pts( local_buff, tmp_buff );
00698               (*size_local_buff) = size_tmp_buff;*/
00699             }            
00700         }
00701     /*} */
00702 
00703   /* now try to reduce wmin */
00704   copy_ring( ering, &r );
00705   /*for( n=0; n<10; n++ )
00706     { */         
00707       while( r.wmin + delta < 0.0) 
00708         {
00709           r.wmin += delta;
00710           size_tmp_buff = 0;
00711           (*label)++;
00712         
00713           log_nfa_new = ell_ring_nfa( angles, used, &r, foci, x, y, grad_dir,
00714                                       *tmp_buff, &size_tmp_buff, *label,
00715                                       logNT_ell );
00716           if( log_nfa_new > log_nfa )
00717             {
00718               ering->wmin += delta;
00719               log_nfa = log_nfa_new;
00720               /* switch addresses of the pointers to the local and tmp buffer */
00721               /*swap_ptr_pts( local_buff, tmp_buff );
00722               (*size_local_buff) = size_tmp_buff; */
00723             }            
00724         }  
00725    /* } */  
00726   if ( ! check_ell_ring(ering) ) return -logNT_ell;
00727   return log_nfa;
00728 }   
