00736 /*----------------------------------------------------------------------------*/
00739 void add_poly_out( Polygon **poly_out, PolyRect *poly, int poly_count, 
00740                    int *poly_count_max, int **labels, int lbl )
00741 {
00742  
00743   if( poly_count>*poly_count_max )
00744     {
00745       *poly_out = (Polygon *) realloc ( *poly_out, (2*poly_count) *
00746                                                    sizeof(Polygon) );
00747    
00748       *labels = (int *) realloc ( *labels, (2*poly_count) * sizeof(int) );
00749       *poly_count_max = 2 * poly_count;
00750     }
00751   
00752   polyrect2polygon( poly, &( (*poly_out)[poly_count-1] ) );
00753   (*labels)[poly_count-1] = lbl;
00754 }
00755 
00756 
00757 /*----------------------------------------------------------------------------*/
00760 void add_ell_out( Ring **ell_out, Ring *ell, int ell_count, int *ell_count_max,
00761                   int **labels, int lbl )
00762 {
00763   if( ell_count>*ell_count_max )
00764     {
00765       *ell_out = ( Ring *) realloc ( *ell_out, (2*ell_count) * sizeof(Ring) );
00766       *labels =  ( int *) realloc ( *labels, (2*ell_count) * sizeof(int) );
00767       *ell_count_max = 2 * ell_count;
00768     }
00769   
00770   copy_ring( ell, &( (*ell_out)[ell_count-1] ) );
00771   (*labels)[ell_count-1] = lbl;
00772 }
