00774 
00775 /*----------------------------------------------------------------------------*/
00780 void ELSDc( PImageDouble in, int *ell_count, Ring **ell_out, int **ell_labels,
00781             int *poly_count, Polygon **poly_out, int **poly_labels, 
00782             PImageInt out )
00783 {
00784   double ang_th = 22.5;     /* gradient angle tolerance in degrees */
00785   double prec;              /* radian precision */
00786   double p;
00787   double rho = 5.0;         /* pixels with gradient magnitude under this 
00788                                threshold are not considered, as their (weak) 
00789                                orientation might be too affected by noise  */
00790   int grad_dirc;            /* direction of the gradient wrt center of the 
00791                                circle */
00792   int grad_dire;            /* direction of the gradient wrt center of the 
00793                                ellipse */
00794   double density_th = 0.7;  /* minimum density */
00795   void *mem_p;              /* parameters used for sorting */ 
00796   int n_bins = 1024;        /* (binning) pixels according to */  
00797   double max_grad = 255.0;  /* to their gradient magnitude */
00798   CoordList *list_p;        /* list of sorted pixels */
00799   unsigned int xsize,ysize; /* image size */
00800   double mlog10eps = 0.0;   /* minus log of the number of accepted false 
00801                                positives ( eps=1 ) */
00802   int min_size_seg;         /* minimum size of a region that could contain a 
00803                                meaningful segment */
00804   int min_size_ell;         /* minimum size of a region that could contain a 
00805                                meaningful ellipse */
00806   int min_size_poly;        /* minimum size of a region that could contain a 
00807                                meaningful ellipse */
00808   double msize;             /* max value allowed for circle/ellipse radius */
00809   double logNT_ell;         /* log of the number of tests for elliptical arcs */
00810   double logNT_seg;         /* log of the number of tests for line segments */ 
00811   double logNT_poly;        /* log of the number of tests for polygons */
00812   double log_nfa, best_nfa; /* NFA values using the continuous */
00813   void *best_feature;       /* pointer to best feature found so far */
00814   char best_type = '0';     /* type of best feature; 'p' for polygon, 
00815                                'e' for ellipse */
00816   PImageDouble imgauss;     /* smooth version of the original image, used during
00817                                candidate selection */
00818   PImageDouble angles;      /* gradient angles smooth image */
00819   PImageDouble angles0;     /* gradient angles original image */
00820   PImageDouble gradmag;     /* gradient magnitude */
00821   PImageDouble gradx, grady;/* gradient orientations on Ox and Oy */
00822   PImageInt used;           /* image to mark already used points */
00823   PolyRect *poly;           /* parameters of polygon as list of rectangles */
00824   PolyRect *seg;            /* one rectangle is a particular polygon */
00825   Ring ering, cring;        /* ring parameters */
00826   double *buff_fit;         /* buffer zone needed for fitting */
00827   int size_buff_fit;        /* size of the currently allocated memory for 
00828                                the buffer */
00829   Point *reg;               /* list of points gathered during curve growing */
00830   int reg_size;
00831   Point *best_buff;         /* buffer to store the points of the most meaningful
00832                                primitive found so far (among segment, polygon, 
00833                                ellipse, circle) in one iteration */
00834   int size_best_buff;
00835   Point *new_buff;          /* buffer to store the points of a new primitive; 
00836                                if the new primitive is more meaningful than 
00837                                the most meaningful primitive found so far, 
00838                                deem new primitive as most meaningful by 
00839                                switching the addresses with best_buff   */
00840   int size_new_buff;        
00841   Point *tmp_buff;          /* buffer to store the points of a new version of a
00842                                primitive, computed during improve operation; 
00843                                if the new version is more meaningful than 
00844                                the most meaningful version found so far, 
00845                                deem new version as most meaningful by 
00846                                switching the addresses with new_buff   */
00847   int ell_count_max = 0;
00848   int poly_count_max = 0;
00849   int adr;
00850   double cparam[5];
00851   double eparam[5]; 
00852   double pext1[2], pext2[2], ang0;
00853   int label = 2;            /* 0 is NOTUSED, 1 is USED, so 'label' must go 
00854                                upward from 2 */              
00855   int label_meaningful = 0;
00856   double spir;
00857   double foci[4];
00858   /*int i;*/
00859 
00860   /* check parameters */
00861   if( (in == NULL) || (in->data == NULL) ) error("ELSDc: invalid input image.");
00862   if( (out == NULL) || (out->data == NULL) ) 
00863     error("ELSDc: invalid output label image.");
00864 
00865   /* allocate and init variables */
00866   prec = M_PI*ang_th/180.0;
00867   p = ang_th/180.0;
00868   xsize = in->xsize;
00869   ysize = in->ysize;
00870   
00871   /* allocate matrix to store elements of the design matrix used in conic
00872      fitting */
00873   buff_fit = (double*) malloc ( sizeof (double) );
00874   size_buff_fit = 1;
00875 
00876   /* allocate buffers to store point coordinates */
00877   reg = (Point *) malloc( xsize * ysize * sizeof(Point) );
00878   if( reg == NULL ) error("ELSDc: not enough memory.");
00879   best_buff = (Point *) malloc( xsize * ysize * sizeof(Point) );
00880   if( best_buff == NULL ) error("ELSDc: not enough memory.");
00881   new_buff = (Point *) malloc( xsize * ysize * sizeof(Point) );
00882   if( new_buff == NULL ) error("ELSDc: not enough memory.");
00883   tmp_buff = (Point *) malloc( xsize * ysize * sizeof(Point) );
00884   if( tmp_buff == NULL ) error("ELSDc: not enough memory.");
00885 
00886   used = new_PImageInt_ini( xsize, ysize, NOTUSED );
00887 
00888   /* Number of tests for ellipse arcs. We must count (3*xsize)*(3*ysize) 
00889      possible values for the center, as center can be outside the image too,
00890      (xsize*ysize) possible values for the two axes, sqrt(xsize*ysize) possible
00891      values for orientation, sqrt(xsize*ysize) possible values for width, 
00892      (xsize*ysize) possible values for delimiting angles. This makes 
00893      9*(xsize*ysize)^4. Since we have polygons and ellipses, i.e. two families,
00894      we must multiply this by 2. So NT_ell = 2*9*(xsize*ysize)^4. 
00895      In log, we get:   */
00896   logNT_ell = 4.0 * ( log10( (double)xsize ) + log10( (double)ysize ) ) + 
00897                       LOG10_9 + LOG10_2; 
00898   min_size_ell = (int)( (-logNT_ell-mlog10eps) / log10(p) );
00899 
00900   logNT_seg = 2.5 * (log10((double)xsize) + log10((double)ysize) ) 
00901                      + LOG10_2;
00902   min_size_seg = (int)( (-logNT_seg - mlog10eps) / log10(p) );
00903 
00904   /* perform gaussian smoothing */
00905   imgauss = gaussian_sampler( in, 1.0, 0.6 );
00906 
00907   /* compute gradient magnitude and orientation  for the smooth image */
00908   img_gradient_sort( imgauss, rho, &list_p, &mem_p, n_bins, max_grad, &angles, 
00909                      &gradmag, &gradx, &grady );
00910 
00911   /* compute gradient orientation for the original image */
00912   angles0 = img_gradient_angle( in, rho );
00913  
00914   /* input image not needed any more; free it */
00915   free_PImageDouble(in);
00916   
00917   msize = max(xsize,ysize);
00918 
00919   poly = new_polyrect();
00920   seg = new_polyrect();
00921 
00922   
00923   /* begin primitive detection */
00924   for( ; list_p; list_p = list_p->next )
00925     { 
00926       adr = list_p->y * used->xsize + list_p->x;
00927       /* only pixels that haven't been used already can be seed pixels */
00928       if( used->data[adr] == USED ) continue;
00929       
00930       /* only pixels with defined gradient can be seed pixels */
00931       if( angles->data[adr] == NOTDEF ) continue;
00932       
00933       /* only pixels that are local maxima in the image can be seed pixels */  
00934       /*if( !local_max( imgauss, list_p->x, list_p->y ) ) continue; */
00935      
00936       /* init intra-iteration variables */  
00937       log_nfa = best_nfa = mlog10eps;
00938       best_feature = NULL; 
00939       best_type = '0';
00940       size_best_buff = size_new_buff = 0;
00941       label++;
00942       spir = 0.0; 
00943       clear_polyrect(poly);
00944       clear_polyrect(seg);
00945           
00946       /* set initial seed point */
00947       reg[0].x = list_p->x; 
00948       reg[0].y = list_p->y;
00949       reg_size = 1;
00950 
00951       /* Gather points aligned along a convex polygon */
00952       if( !curve_grow( gradmag, angles, used, reg, &reg_size, density_th,
00953                        prec, poly, &label, pext2, pext1, &spir ) ) 
00954         continue;
00955 
00956       
00957       /* Validate polygon */
00958       /* Compute number of tests for polygon; it must be done for each 
00959          polygon, as it depends on the number of segments in the 
00960          polygon */
00961       logNT_poly = ( poly->dim + 1.5 ) * ( log10((double)xsize ) + 
00962                      log10( (double)ysize) ) + ( poly->dim+1.0 ) * LOG10_2;
00963       /* polygon's best validation score */
00964       min_size_poly =(int)((-logNT_poly - mlog10eps)/log10(p));
00965       
00966       if( reg_size > min_size_poly )
00967         {
00968           best_nfa = poly_improve( angles0, used, poly, &best_buff, 
00969                                    &size_best_buff, &tmp_buff, logNT_poly );
00970           best_feature = (void*)poly;
00971           best_type = 'p';
00972         }         
00973 
00974       /* Estimate a single segment on the entire set and validate; 
00975          this prevents from considering very small regions at the ends 
00976          of a large segment as new segments. */
00977       get_seg( angles, gradmag, used, seg, reg, reg_size, prec );
00978       log_nfa = poly_improve( angles0, used, seg, &new_buff, 
00979                               &size_new_buff, &tmp_buff, logNT_seg );
00980     
00981       /* if more meaningful than previous, update best feature */
00982       if( log_nfa > best_nfa && size_new_buff>min_size_seg ) 
00983         {
00984           best_nfa = log_nfa;
00985           best_feature = (void*)seg;
00986           best_type = 'p';
00987           swap_ptr_pts( &best_buff, &new_buff );
00988           size_best_buff = size_new_buff;
00989         }
00990 
00991       /* Estimate circle and ellipse parameters (centre, axes, orientation)
00992          on the gathered pixels */
00993       if( reg_size > min_size_ell ) 
00994         conic_fit( gradx, grady, reg, reg_size, &buff_fit, &size_buff_fit, 
00995                    cparam, eparam );
00996 
00997       /* Get parameters of the circle ring and compute validation score */
00998       ang0 = angles->data[ reg[0].y*angles->xsize + reg[0].x ];
00999       if( get_ring( reg, reg_size, ang0, cparam, pext1, pext2, CIRCLE,
01000                     spir, &cring, &grad_dirc, foci, msize ) )
01001         {
01002           log_nfa = circ_ring_improve( angles0, used, &cring, reg[0].x, 
01003                                        reg[0].y, grad_dirc, &new_buff, 
01004                                        &size_new_buff, &tmp_buff, &label,
01005                                        logNT_ell );
01006           /* if more meaningful than previous, update best feature */
01007           if( log_nfa > best_nfa && size_new_buff>min_size_ell ) 
01008             {
01009               best_nfa = log_nfa;
01010               best_feature = (void*)(&cring);
01011               best_type = 'c';
01012               swap_ptr_pts( &best_buff, &new_buff );
01013               size_best_buff = size_new_buff;
01014             }
01015         } /* ! get cring */
01016    
01017       /* Get parameters of the ellipse ring and compute validation score */
01018       if( get_ring( reg, reg_size, ang0, eparam, pext1, pext2, ELLIPSE,
01019                     spir, &ering, &grad_dire, foci, msize ) )
01020         {
01021           log_nfa = ell_ring_improve( angles0, used, &ering, foci, reg[0].x, 
01022                                       reg[0].y, grad_dire, &new_buff, 
01023                                       &size_new_buff, &tmp_buff, &label,
01024                                       logNT_ell ); 
01025           /* if more meaningful than previous, update best feature */
01026           if( log_nfa > best_nfa && size_new_buff>min_size_ell ) 
01027             {
01028               best_nfa = log_nfa;
01029               best_feature = (void*)(&ering);
01030               best_type = 'c';
01031               swap_ptr_pts( &best_buff, &new_buff );
01032               size_best_buff = size_new_buff;
01033             }
01034         }  /* ! get ering */
01035 
01036       /* At this point, we have the most meaninful feature at this iteration. 
01037          If it is meaningful compared to eps, then store */   
01038       if( best_nfa <= mlog10eps ) continue;
01039       label_meaningful++;
01040       if( best_type == 'c' )
01041         {
01042           (*ell_count)++;
01043           add_ell_out( ell_out, (Ring*)best_feature, *ell_count, &ell_count_max, 
01044                        ell_labels, label_meaningful );
01045           /*Ring *rr = (Ring*)best_feature;*/
01046           /* free(rr); */
01047         }
01048       else if( best_type == 'p' ) 
01049         {
01050           (*poly_count)++;
01051           add_poly_out( poly_out, (PolyRect*)best_feature, *poly_count, 
01052                         &poly_count_max, poly_labels, label_meaningful );
01053         }
01054  
01055 
01056       /* Mark as USED the pixels of the best feature in 'used' */
01057       mark_img_pts( used, best_buff, 0, size_best_buff, USED );
01058 
01059       /* Mark label of the best feature in output label image */
01060       mark_img_pts( out, best_buff, 0, size_best_buff, label_meaningful );  
01061    
01062     }  /* ! for loop */
01063 
01064   /* free useless structures and arrays */          
01065   free_PImageDouble(imgauss);
01066   free_PImageDouble(gradx); 
01067   free_PImageDouble(grady);
01068   free_PImageDouble(gradmag); 
01069   free_PImageDouble(angles);
01070   free_PImageDouble(angles0);  
01071   free_PImageInt(used);
01072   free(reg); 
01073   free(best_buff);
01074   free(new_buff);
01075   free(tmp_buff);
01076   free(buff_fit);  
01077   free(poly->rectlist); free(poly); 
01078   free(seg->rectlist); free(seg); 
01079   free(mem_p);
01080 }
