00038 void copy_ring( Ring *in, Ring *out )
00039 {
00040   /* check parameters */
00041   if( (in == NULL) || (out == NULL) ) 
00042     error("copy_ring: invalid 'in' or 'out'.");
00043 
00044   out->x1 = in->x1;
00045   out->y1 = in->y1;
00046   out->x2 = in->x2;
00047   out->y2 = in->y2;
00048   out->width = in->width;
00049   out->cx = in->cx;
00050   out->cy = in->cy;
00051   out->theta = in->theta;
00052   out->ax = in->ax;
00053   out->bx = in->bx;
00054   out->ang_start = in->ang_start;
00055   out->ang_end   = in->ang_end;
00056   out->wmin = in->wmin;
00057   out->wmax = in->wmax;
00058   out->full = in->full;
00059 }
