00062 /*----------------------------------------------------------------------------*/
00065 void swap_ring( Ring *r )
00066 {
00067   /* check parameters */
00068   if( r == NULL ) error("swap_ring: invalid ring.");
00069 
00070   swap_double( &(r->x1), &(r->x2) );
00071   swap_double( &(r->y1), &(r->y2) );
00072   swap_double( &(r->ang_start), &(r->ang_end) );
00073   r->full = 1;
00074 }
