00079 int check_circ_ring( Ring *cring )
00080 {
00081   /* check parameters */
00082   if( cring == NULL ) error("check_circ_ring: invalid ring.");
00083 
00084   /* reject if degenerate ellipse */
00085   if( cring->wmin*cring->wmax > 0 ) return FALSE;
00086   return TRUE;
00087 }
