00106 /*----------------------------------------------------------------------------*/
00109 int check_circ( double *cparam )
00110 {
00111   /* check parameters */
00112   if( cparam == NULL ) error("check_circ: invalid circle.");
00113 
00114   /* reject if degenerate circle (negative or close to 0 radius) */
00115   if( cparam[2] <= 1 ) return FALSE;
00116   /* reject directly if very small arc, to win time */
00117   /* if( angle_diff( cring->ang_end, cring->ang_start) < 0.14 ) return FALSE;*/
00118   return TRUE;
00119 }
