00122 /*----------------------------------------------------------------------------*/
00126 int check_ell( double *eparam )
00127 {
00128   /* check parameters */
00129   if( eparam == NULL ) error("check_ell: invalid ellipse.");
00130 
00131   /* reject if degenerate ellipse (negative axes) */
00132   if( (eparam[2] <= 1) || (eparam[3] <= 1) ) return FALSE;
00133   /* make sure 'ax' is the greater axis */
00134   if( eparam[2] < eparam[3] )
00135     {
00136       swap_double( &(eparam[2]), &(eparam[3]) );
00137       eparam[4] += M_1_2_PI;
00138     }
00139   /* reject directly if very thin ellipse, to win time */
00140   if( eparam[2] / eparam[3] > 100 ) return FALSE;
00141 
00142   /* make sure orientation belongs to [-pi/2, pi/2] */
00143   if( eparam[4] > M_1_2_PI ) eparam[4] -= M_PI;
00144   if( eparam[4] > M_PI ) eparam[4] -= M_PI;
00145   if( eparam[4] < -M_1_2_PI ) eparam[4] += M_PI;
00146 
00147   return TRUE;
00148 }
