00151 /*----------------------------------------------------------------------------*/
00156 int is_in_circ_ring( Ring * r, int x, int y )
00157 {
00158   /* check parameters */
00159   if( r == NULL ) error("is_in_ring: invalid input ring.");
00160 
00161   double d = dist( x, y, r->cx, r->cy) - r->ax;
00162 
00163   double theta = atan2( (double)y - r->cy, (double)x - r->cx);
00164 
00165   /* make sure angle in [0, 2pi] */
00166   if( theta<0 ) theta += M_2__PI;
00167 
00168   if( r->ang_end > r->ang_start ) 
00169     {
00170       if( (theta < r->ang_start) || (theta > r->ang_end) ) return FALSE;
00171     }
00172   else
00173     if( (theta < r->ang_start) && (theta > r->ang_end) ) return FALSE;
00174 
00175   if( (d < r->wmin) || (d > r->wmax) ) return FALSE;
00176   
00177   return TRUE;
00178 }
