00181 /*----------------------------------------------------------------------------*/
00186 int is_in_ell_ring( Ring * r, int x, int y )
00187 {
00188   /* check parameters */
00189   if( r == NULL ) error("is_in_ring: invalid input ring.");
00190 
00191   double d = d_rosin( r, (double)x, (double)y );
00192 
00193   double theta = atan2( (double)y - r->cy, (double)x - r->cx);
00194 
00195   /* make sure angle in [0, 2pi] */
00196   if( theta < 0 ) theta += M_2__PI;
00197   
00198   if( r->ang_end > r->ang_start ) 
00199     {
00200       if( (theta < r->ang_start) || (theta > r->ang_end) ) return FALSE;
00201     }
00202   else
00203     if( (theta < r->ang_start) && (theta > r->ang_end) ) return FALSE;
00204 
00205   if( (d < r->wmin) || (d > r->wmax) ) return FALSE;
00206   return TRUE;
00207 }
