00210 /*----------------------------------------------------------------------------*/
00214 void rosin_point( Ring *ering, int x, int y, double *xi, double *yi )
00215 {  
00216   double xtmp, ytmp;
00217   double ae2, be2, fe2; 
00218   double xp, yp, xp2, yp2, xx, yy;  
00219   double delta, A, ah, bh2, term;
00220   double d[4];
00221   int pos;
00222   xtmp = x - ering->cx;
00223   ytmp = y - ering->cy;
00224   ae2  = ering->ax * ering->ax;
00225   be2  = ering->bx * ering->bx;
00226   fe2 = ae2 - be2;
00227   xp = xtmp * cos(-ering->theta) - ytmp * sin(-ering->theta);
00228   yp = xtmp * sin(-ering->theta) + ytmp * cos(-ering->theta);
00229   xp2 = xp * xp;
00230   yp2 = yp * yp;
00231   delta = (xp2 + yp2 + fe2) * (xp2 + yp2 + fe2) - 4 * xp2 * fe2;
00232   A = (xp2 + yp2 + fe2 - sqrt(delta))/2.0; 
00233   ah = sqrt(A);
00234   bh2 = fe2 - A;
00235   term = (A * be2 + ae2 * bh2);
00236   xx = ah * sqrt( ae2 * (be2 + bh2) / term );
00237   yy = ering->bx * sqrt( bh2 * (ae2 - A) / term );
00238 
00239   d[0] = dist( xp, yp, xx, yy );
00240   d[1] = dist( xp, yp, xx,-yy );
00241   d[2] = dist( xp, yp,-xx, yy );
00242   d[3] = dist( xp, yp,-xx,-yy );
00243 
00244   min_array_pos( d, 4, &pos );
00245   switch(pos)
00246     { 
00247       case 0: break;
00248       case 1: yy = -yy; break;
00249       case 2: xx = -xx; break;
00250       case 3: xx = -xx; yy = -yy; break;
00251       default: break;
00252     }
00253   *xi = xx * cos(ering->theta) - yy * sin(ering->theta) + ering->cx;
00254   *yi = xx * sin(ering->theta) + yy * cos(ering->theta) + ering->cy;
00255 }
