00258 /*----------------------------------------------------------------------------*/
00262 double d_rosin( Ring *ering, double x, double y )
00263 { 
00264   double xtmp, ytmp;
00265   double ae2, be2, fe2; 
00266   double xp, yp, xp2, yp2, xx, yy;  
00267   double delta, A, ah, bh2, term;
00268   double d[4];
00269 
00270   xtmp = x - ering->cx;
00271   ytmp = y - ering->cy;
00272   ae2  = ering->ax * ering->ax;
00273   be2  = ering->bx * ering->bx;
00274   fe2 = ae2 - be2;
00275   xp = xtmp * cos(-ering->theta) - ytmp * sin(-ering->theta);
00276   yp = xtmp * sin(-ering->theta) + ytmp * cos(-ering->theta);
00277   xp2 = xp * xp;
00278   yp2 = yp * yp;
00279   delta = (xp2 + yp2 + fe2) * (xp2 + yp2 + fe2) - 4 * xp2 * fe2;
00280   A = (xp2 + yp2 + fe2 - sqrt(delta))/2.0; 
00281   ah = sqrt(A);
00282   bh2 = fe2 - A;
00283   term = (A * be2 + ae2 * bh2);
00284   xx = ah * sqrt( ae2 * (be2 + bh2) / term );
00285   yy = ering->bx * sqrt( bh2 * (ae2 - A) / term );
00286 
00287   d[0] = dist( xp, yp, xx, yy);
00288   d[1] = dist( xp, yp, xx,-yy);
00289   d[2] = dist( xp, yp,-xx, yy);
00290   d[3] = dist( xp, yp,-xx,-yy);
00291   double dmin = min_array(d,4); 
00292   if( xp2+yp2 > xx*xx+yy*yy ) return dmin;
00293   else return -dmin; 
00294 }
