00297 /*----------------------------------------------------------------------------*/
00300 void ellipse_foci( double *param, double *foci )
00301 {
00302   double f;
00303   /* check parameters */
00304   if( param == NULL) error("ellipse_foci: invalid input ellipse.");
00305   if( foci == NULL) error("ellipse_foci: 'foci' must be non null.");
00306 
00307   f = sqrt( param[2] * param[2] - param[3] * param[3] );
00308   foci[0] = param[0] + f * cos(param[4]);
00309   foci[1] = param[1] + f * sin(param[4]);
00310   foci[2] = param[0] - f * cos(param[4]);
00311   foci[3] = param[1] - f * sin(param[4]);
00312 }
