00315 /*----------------------------------------------------------------------------*/
00319 double ellipse_normal_angle( double x, double y, double *foci )
00320 {
00321   double tmp1, tmp2, tmp3, theta;
00322   /* check parameters */
00323   if( foci == NULL ) error("ellipse_normal_angle: 'foci' must be non null");
00324 
00325   tmp1 = atan2( y-foci[1], x-foci[0]);
00326   tmp2 = atan2( y-foci[3], x-foci[2]);
00327   tmp3 = angle_diff_signed( tmp1, tmp2 );
00328 
00329   theta = tmp1 - tmp3/2.0;
00330   while( theta <= -M_PI ) theta += M_2__PI;
00331   while( theta >   M_PI ) theta -= M_2__PI;
00332   return theta;
00333 }
