00336 /*----------------------------------------------------------------------------*/
00341 static int dir_gradient_circ( int px, int py, double ang, double *param )
00342 {
00343   double a;
00344   int dir = 0;
00345   /* check parameters */
00346   if( param == NULL ) error("dir_gradient_circ: invalid param.");
00347 
00348   a = atan2( py- param[1], px-param[0] );
00349   if( angle_diff( a, ang ) < M_1_2_PI )
00350     dir = 1;
00351   return dir;
00352 }
