00355 /*----------------------------------------------------------------------------*/
00360 static int dir_gradient_ell( int px, int py, double ang, double *foci )
00361 {
00362   double a;
00363   int dir = 0;
00364   /* check parameters */
00365   if( foci == NULL ) error("dir_gradient_ell: invalid ellipse.");
00366   a = ellipse_normal_angle( (double)px, (double)py, foci );
00367   if( angle_diff( a, ang ) < M_1_2_PI )
00368     dir = 1;
00369   return dir;
00370 }
