00373 /*----------------------------------------------------------------------------*/
00377 static void circ_ring_width( Point *reg, int reg_size, Ring *cring )
00378 {
00379   int i;
00380   double w;
00381   double wmin = DBL_MAX;
00382   double wmax = -DBL_MAX;
00383   /* check parameters */
00384   if( reg == NULL ) error("circ_ring_width: invalid region.");
00385   if( cring == NULL ) error("circ_ring_width: invalid ring.");
00386   
00387   /* compute widths */ 
00388   for( i=0; i<reg_size; i++ )
00389     {  
00390       w = dist( reg[i].x, reg[i].y, cring->cx, cring->cy) - cring->ax;    
00391       if( w<wmin ) wmin = w;
00392       if( w>wmax ) wmax = w;
00393     }
00394   cring->wmin = wmin; cring->wmax = wmax;
00395 }
