00398 /*----------------------------------------------------------------------------*/
00402 static void ell_ring_width( Point *reg, int reg_size, Ring *ering )
00403 {
00404   int i;
00405   double w;
00406   double wmin = DBL_MAX;
00407   double wmax = -DBL_MAX;
00408   /* check parameters */
00409   if( reg == NULL ) error("ell_ring_width: invalid region.");
00410   if( ering == NULL ) error("ell_ring_width: invalid ring.");
00411   
00412   /* compute widths */ 
00413   for( i=0; i<reg_size; i++ )
00414     {  
00415       w = d_rosin( ering, (double)reg[i].x, (double)reg[i].y );
00416       if( w<wmin ) wmin = w;
00417       if( w>wmax ) wmax = w;
00418     }
00419   ering->wmin = wmin; ering->wmax = wmax;
00420 }
