00423 /*----------------------------------------------------------------------------*/
00427 int get_ring( Point *reg, int reg_size, double ang, double *param, 
00428               double *pext1, double *pext2, int conic, double spir, Ring *r, 
00429               int *grad_dir, double *foci, int msize )
00430 {
00431   double tang;
00432   /* check parameters */
00433   if( reg == NULL ) error("get_ring: invalid region."); 
00434   if( param == NULL ) error("get_ring: invalid conic parameters."); 
00435   if( r == NULL ) error("get_ring: ring must be non null.");
00436   if( foci == NULL ) error("get_ring: foci must be non null."); 
00437   
00438   /* fill in centre */
00439   r->cx = param[0]; r->cy = param[1];
00440 
00441   /* Init 'full' flag; by default, set to 0. */
00442   r->full = 0;
00443 
00444   if( conic == CIRCLE ) 
00445     {
00446       if( !check_circ(param) ) return FALSE;
00447       *grad_dir = dir_gradient_circ( reg[0].x, reg[0].y, ang, param );   
00448     }
00449   else
00450     {
00451       if( !check_ell(param) ) return FALSE;
00452       ellipse_foci( param, foci );
00453       *grad_dir = dir_gradient_ell( reg[0].x, reg[0].y, ang, foci );
00454     }
00455   /* fill in axes and orientation: this must be done after check, as for the 
00456      ellipse the axes might have been swapped, and orientation normalized */
00457   r->ax = param[2]; r->bx = param[3];
00458   r->theta = param[4];
00459 
00460   /* fill in extremal points */
00461   r->x1 = pext1[0]; r->y1 = pext1[1];
00462   r->x2 = pext2[0]; r->y2 = pext2[1]; 
00463   
00464   /* if gradient converges, interchange the extremal points to keep 
00465      trigonometric sense */    
00466   if( *grad_dir == 0 )
00467     {
00468       swap_double( &(r->x1), &(r->x2) );
00469       swap_double( &(r->y1), &(r->y2) );
00470     } 
00471   
00472   /* set delimiting angles */
00473   r->ang_start = atan2( r->y1 - r->cy, r->x1 - r->cx );
00474   if( r->ang_start < 0 ) r->ang_start += M_2__PI; 
00475   r->ang_end = atan2( r->y2 - r->cy, r->x2 - r->cx );
00476   if( r->ang_end < 0 ) r->ang_end += M_2__PI;
00477 
00478   /* if the ends of the last rectangles got interwined, swap the ends 
00479      of the ring  */
00480   if( r->ang_start > r->ang_end ) tang = r->ang_end + M_2__PI;
00481   else tang = r->ang_end;      
00482   if( (spir > M_PI) && (tang - r->ang_start < M_1_2_PI/2.0) ) 
00483     swap_ring( r );
00484   
00485   /* set ring widths */
00486   if( conic == CIRCLE ) 
00487     circ_ring_width( reg, reg_size, r );
00488   else
00489     ell_ring_width( reg, reg_size, r );
00490 
00491   /* abnormal case: width of the ring is bigger than little axis -> 
00492      discard ring */  
00493   if( r->wmax - r->wmin > r->bx ) return FALSE;
00494   if( r->ax > msize ) return FALSE;
00495   
00496   return TRUE;
00497 }
